if SERVER thenutil.AddNetworkString( 'chat_left' )function left(ply)net.Start( 'chat_left' )net.WriteString(ply:Nick())net.WriteString(ply:SteamID())net.Broadcast()endhook.Add("PlayerDisconnected", "pldis", left)util.AddNetworkString( 'chat_join' )function join(ply)net.Start( 'chat_join' )net.WriteString(ply:Nick())net.WriteString(ply:SteamID())net.Broadcast()endhook.Add("PlayerInitialSpawn", "pljoin", join)endif CLIENT thenfunction left(leng, ply) local text = net.ReadString()local text2 = net.ReadString()local color1 = Color(227, 222, 222, 255)local color2 = Color(227, 222, 222, 255)local color3 = Color(227, 222, 222, 255)local color4 = Color(227, 222, 222, 255)chat.AddText(color4,"[Disconnected]: ", color3, "Игрок ", color2, text, color3," вышел.  ", color1, text2)endnet.Receive( 'chat_left', left)function join(leng, ply)local text = net.ReadString()local text2 = net.ReadString()local color1 = Color(227, 222, 222, 255)local color2 = Color(227, 222, 222, 255)local color3 = Color(227, 222, 222, 255)local color4 = Color(227, 222, 222, 255)chat.AddText(color4, "[Connect]: ", color3, "Игрок ", color2, text, color3," присоединился. SteamID - ", color1, text2)endnet.Receive( 'chat_join', join)end print ( "" )